    this.getStamps = showUI => {
      this.selectedAnnotation.stampDetailsArray = [];
      let stampsArray = this.eViewerObj.annotationService.getStamps();
      this.selectedAnnotation.stampDetailsArray.push(stampsArray);
      if (this.selectedAnnotation.stampDetailsArray[0].length !== 0 && showUI === true) {
        setTimeout(() => {
          console.log(stampsArray);
          this.setState({
            imageStampDiv: true
          });
        }, 50);
      }
    };
    this.ChooseStamp = event => {
      const stampValue = event.target.value;
      const stampDetails = this.selectedAnnotation.stampDetailsArray;
      const stampInfo = {
        stampType: "imageStamp",
        info: []
      };
      for (let stamp of stampDetails[0][0]) {
        if (stamp.stampName === stampValue) {
          stampInfo.info.push(stamp);
        }
      }
      switch (stampValue) {
        case "Approved":
        case "Question":
        case "Warning":
        case "Confidential":
        case "Denied":
        case "Initial":
        case "Initial Left":
        case "Initail Right":
        case "Pending":
        case "Received":
        case "SignHere Left":
        case "SignHere Right":
        case "Urgent":
          this.selectedAnnotation.name = "imageStamp";
          this.eViewerObj.annotationService.selectShape(this.selectedAnnotation.name, stampInfo);
          this.setState({
            imageStampDiv: false
          });
          this.setState({
            multiPageCoordinateAnnotationSelected: true
          });
          break;
        default:
          break;
      }
    };
    this.MultiPageAnnotation = event => {
      this.eViewerObj = null;
      if (this.eViewerObj === null) {
        this.eViewerObj = new eViewerApp(this.props.userName);
      }
      let annType;
      annType = event.target.value;
      if (annType != "stamp") this.selectedAnnotation.name = annType;
      if (annType === "stamp") {
        this.setState({
          multiPageStampAnnotationSelected: true
        });
        this.setState({
          multiPageCoordinateAnnotationSelected: false
        });
        this.selectedAnnotation.name = null;
      } else if (annType === "imageStamp") {
        this.setState({
          multiAnnotationDiv: false
        });
        this.setState({
          imageStampDiv: true
        });
        this.getStamps(true);
      } else if (annType === "Select Annotation") {
        this.setState({
          multiPageCoordinateAnnotationSelected: false
        });
        this.setState({
          multiPageStampAnnotationSelected: false
        });
      } else {
        this.setState({
          multiPageCoordinateAnnotationSelected: true
        });
        this.setState({
          multiPageStampAnnotationSelected: false
        });
        this.eViewerObj.annotationService.selectShape(annType);
      }
    };
