      }
      let redactWord = event.target[0].value;
      let wholeWord = event.target[1].value;
      let caseSenstive = event.target[2].value;
      if (redactWord == "") {
        return;
      }
      if (wholeWord == "" || wholeWord == "false") {
        wholeWord = false;
