      if (caseSenstive == "" || caseSenstive == "false") {
        caseSenstive = false;
      } else {
        caseSenstive = true;
      }
      let options = {
        wholeWord: wholeWord,
        caseSensitive: caseSenstive,
        selectedTag: event.target[3].value
      };
      this.eViewerObj.redactionService.redactWord(redactWord, options);
      this.disableAllDiv();
