import React, { Component } from "react";
import "./App.css";
import eViewerApp from "@mstechusa/eviewer7/js/eViewer7";
import "./js/events";
import uuid from "react-uuid";

class App extends Component {
  constructor(props) {
    super(props);
    this.docUrlArray = [];
    this.annUrlArray = [];
    this.docIndex = 0;
    this.licenseKey = this.props.licenseKey;
    this.eViewerObj = null;
    this.customIcon =
      "data:image/png;base64,";
    this.customIcon1 =
      "data:image/png;base64,";
    this.eViewerObj = null;
    this.state = {
      value: "uploadDocument",
      defaultDisabled: false,
      isUploadDivShow: false,
      isGetDocumentInfo: false,
      isGetPageInfo: false,
      isGetCurrentScale: false,
      isGetCurrentRotation: false,
      isAddButtonShow: false,
      isRemoveButtonShow: false,
      isFilterPagesShow: false,
      isShowOnlyPagesShow: false,
      isHideOnlyPagesShow: false,
      isUpdateButtonShow: false,
      isgotoPageDivShow: false,
      isinsertDivShow: false,
      isappendDivShow: false,
      issearchTextDivShow: false,
      isExportDivShow: false,
      isInvertPageDivShow: false,
      isCopyToClipboardDivShow: false,
      isSetCustomStampsShow: false,
      isSetDocumentTabStyle: false,
      isGetDocumentTabStyle: false,
      isSetDocumentNameTooltipDirection: false,
      showAddWatermarkForm: false,
      showEditWatermarkForm: false,
      showDeleteWatermarkForm: false,
      isLinkAnnotationShow: false,
      scrollMode: "continuous",
      showRedactAreaForm: false,
      showRedactWordsForm: false,
      showClearRedactionForm: false,
      showRedactionDetailsForm: false,
      redactByExpressionTag: false,
      isSetScrollModeDivShow: false,
      isPrintDivShow: false,
      isSnippingToolDivShow: false,
      isDownloadDocDivShow: false,
      isShowSetDocMetadata: false,
    };

    this.state = { filterJSON: "" };
    this.state = { showHideOnlyPagesFilter: "" };

    this.state = { startPage: 0, endPage: 0 };
    this.state = {
      docURLs: "",
      annURLs: "",
      captionURLs: "",
      // userName: "",
    };
    this.state = {
      docId: "",
      pageRange: "",
    };
    this.state = {
      doctype: "",
      pageOption: "",
      DocName: "",
      DocMetaDataJson: "",
    };
    this.state = {
      pageNO: 0,
    };
    this.state = {
      fileURL: null,
      file: null,
      captionFiles: null,
    };
    this.state = {
      textSearch: "",
    };

    this.state = {
      fileNameValue: "",
    };

    this.state = { landingPgNo: 1 };
    this.state = { hidePages: "", clientDocId: "" };

    this.selectedAnnotation = {
      name: "",
      startX: 0,
      endX: 0,
      startY: 0,
      endY: 0,
      option: "",
      annID: "",
      replyText: "",
      stampDetailsArray: [],
      comment: false,
      reply: false,
      addReply: false,
    };
  }
  componentDidMount() {
    this.loadViewer();
  }

  async loadViewer() {
    this.eViewerObj = new eViewerApp(this.props.userName);

    const options = {
      contextMenuOptions: {
        overrideContextMenus: this.props.overrideCtxMenu,
        location: [
          "documentView",
          "pageThumbnails",
          "docThumbnails",
          "toolbar",
          "docTab",
        ],
      },
    };

    this.eViewerObj
      .loadViewer("eviewer", null, null, "bestFit", options)
      .then(() => {
        console.log("loading viewer successfully");
        this.eViewerObj.registerLicense(
          this.licenseKey,
          this.props.viewerServerURL
        );
      });
    await import("@mstechusa/eviewer7/styles.css");
    await import("@mstechusa/eviewer7/scripts");
    await import("@mstechusa/eviewer7/runtime");
    await import("@mstechusa/eviewer7/polyfills");
    await import("@mstechusa/eviewer7/main");
    // await import("@mstechusa/eviewer7/js/events");

    setTimeout(() => {
      // this.setViewerOptions();
      if (this.selectedAnnotation.stampDetailsArray.length === 0) {
        // Not required but had to do to initialize the internal arrays o eviewer for use case for QA whrer they upload documents with upload component eithout api's.
        this.eViewerObj.annotationService.getStamps();
      }
      this.eViewerObj.onDocumentTabHover((viewerDocID, inFocus) => {
        console.log("viewerDocID " + viewerDocID);
        console.log("inFocus " + inFocus);
      });
    }, 100);
  }

  submitUploadDetail = (event) => {
    if (this.eViewerObj === null) {
      this.eViewerObj = new eViewerApp(this.props.userName);
    }

    this.eViewerObj.setUserName(this.props.userName);
    let annUrl = this.state.annURLs;
    if (annUrl == undefined) {
      annUrl = null;
    } else {
      this.annUrlArray = this.state.annURLs.split(";");

      if (
        this.annUrlArray[this.docIndex] !== undefined &&
        this.annUrlArray[this.docIndex] !== null
      ) {
        annUrl = this.annUrlArray[this.docIndex];
      } else {
        annUrl = null;
      }
    }
    let documentSrvc = this.eViewerObj.getDocumentService();

    // let clientDocID = "client_" + uuid();
    this.docUrlArray = this.state.docURLs.split(";");
    this.docIndex = 0;
    // documentSrvc.closeAllDocuments();
    if (this.props.overrideThumbIndicator) {
      documentSrvc
        .loadDocumentWithOptions(
          this.docUrlArray[this.docIndex],
          annUrl,
          this.state.clientDocId,
          //optional parameter
          {
            isEditMode: true,
            repoType: "filesystem",
            password: "",
            landingPage: this.state.landingPgNo,
            pageFilters: this.getPageVisibility(),
            tabStyle: {
              // backgroundColor: "rgb(247, 247, 247)",
              // color: "black",
              // fontWeight: "",
              // fontStyle: "",
              fileName: "some-document-description-OUTFOCUS",
              // icon: "",
            },
            focusTabStyle: {
              // backgroundColor: "rgb(57, 139, 247)",
              // color: "white",
              // fontWeight: "",
              // fontStyle: "",
              fileName: "some-document-description-INFOCUS",
              // icon: "",
            },
            thumbnailIconCallback: (thumbnails) => {
              const thumbArr = Array.from(thumbnails);
              while (thumbnails.length) {
                thumbnails.pop();
              }
              for (let i = 0; i < thumbArr.length; i++) {
                if (thumbArr[i] % 2 === 0) {
                  thumbnails.push({
                    pageNo: thumbArr[i],
                    icon: "data:image/png;base64,",
                    vLocation: "bottom",
                    hLocation: "left",
                    hoverText: "Indicator",
                  });
                } else {
                  thumbnails.push({
                    pageNo: thumbArr[i],
                    icon: "data:image/png;base64,",
                    vLocation: "top",
                    hLocation: "left",
                    hoverText: "indicator 1",
                  });
                }
              }
            },
          }
        )
        .then((response) => {
          console.log(response);
          this.setState({ defaultDisabled: false });
          this.setState({ docURLs: "" });
          this.setState({ landingPgNo: 1 });
        });
    } else {
      documentSrvc
        .loadDocumentWithOptions(
          this.docUrlArray[this.docIndex],
          annUrl,
          this.state.clientDocId,
          //optional parameter
          {
            isEditMode: true,
            repoType: "filesystem",
            password: "",
            landingPage: this.state.landingPgNo,
            pageFilters: this.getPageVisibility(),
            tabStyle: {
              //   backgroundColor: "white",
              //   color: "black",
              //   fontWeight: "900",
              //   fontStyle: "italic",
              fileName: "some-document-description-OUTFOCUS",
              //   icon: "",
            },
            focusTabStyle: {
              //   backgroundColor: "red",
              //   color: "white",
              //   fontWeight: "900",
              //   fontStyle: "underline",
              fileName: "some-document-description-INFOCUS",
              //   icon: "",
            },
          }
        )
        .then((response) => {
          console.log(response);
          this.setState({ defaultDisabled: false });
          this.setState({ docURLs: "" });
          this.setState({ annURLs: "" });
          this.setState({ landingPgNo: 1 });
        });
    }

    if (this.selectedAnnotation.stampDetailsArray.length === 0) {
      this.getStamps(false);
    }
    this.disableAllDiv();
    this.setState({
      isShowDocNavigators: true,
    });
  };

  getPageVisibility = () => {
    if (this.state.hidePages.length === 0) {
      return [];
    }
    const pagesToHide = this.state.hidePages.split(",");
    const response = [];
    for (const pg of pagesToHide) {
      response.push({ pageNo: parseInt(pg, 10), visible: false });
    }
    return response;
  };

  submitGetDocumentInfo = (event) => {
    this.eViewerObj.documentService
      .getDocumentInfo(this.state.docId)
      .then((response) => {
        console.log(response);
      });
  };

  docTabIcon = (events) => {
    var reader = new FileReader();
    if (events.target.files[0] != null) {
      reader.readAsDataURL(events.target.files[0]);

      reader.onload = (events) => {
        this.setState({
          icon: events.target.result,
        });
      };
    } else {
      this.setState({ icon: null });
    }
  };
  docTabFocusIcon = (events) => {
    var reader = new FileReader();
    if (events.target.files[0] != null) {
      reader.readAsDataURL(events.target.files[0]);

      reader.onload = (events) => {
        this.setState({
          focusIcon: events.target.result,
        });
      };
    } else {
      this.setState({ focusIcon: null });
    }
  };

  submitSetDocumentTabStyle = (event) => {
    event.preventDefault();
    var docId = event.target[0].value;
    var backgroundColor = event.target[1].value;
    var color = event.target[3].value;
    var fileName = event.target[5].value;
    var fontWeight = event.target[7].value;
    var icon = this.state.icon;
    var tabFontStyle = null;
    if (event.target[9].checked) {
      tabFontStyle = event.target[9].value;
    }
    var tabTextUnderline = null;
    if (event.target[10].checked) {
      tabTextUnderline = event.target[10].value;
    }
    this.state.icon = null;
    var fontStyle = null;
    if (tabFontStyle != null || tabTextUnderline != null) {
      if (tabFontStyle != null && tabTextUnderline != null) {
        fontStyle = tabFontStyle + " | " + tabTextUnderline;
      } else if (tabFontStyle != null && tabTextUnderline == null) {
        fontStyle = tabFontStyle;
      } else {
        fontStyle = tabTextUnderline;
      }
    } else {
      fontStyle = "normal";
    }

    var focusBackgroundColor = event.target[2].value;
    var focusColor = event.target[4].value;
    var focusfileName = event.target[6].value;
    var focusFontWeight = event.target[8].value;
    var focusTabFontStyle = null;
    if (event.target[11].checked) {
      focusTabFontStyle = event.target[11].value;
    }
    var focusTextUnderline = null;
    if (event.target[12].checked) {
      focusTextUnderline = event.target[12].value;
    }
    var focusIcon = this.state.focusIcon;
    this.state.focusIcon = null;
    var focusFontStyle = null;
    if (focusTabFontStyle != null || focusTextUnderline != null) {
      if (focusTabFontStyle != null && focusTextUnderline != null) {
        focusFontStyle = focusTabFontStyle + " | " + focusTextUnderline;
      } else if (focusTabFontStyle != null && focusTextUnderline == null) {
        focusFontStyle = focusTabFontStyle;
      } else {
        focusFontStyle = focusTextUnderline;
      }
    } else {
      focusFontStyle = "normal";
    }
    var style = {
      backgroundColor: backgroundColor,
      color: color,
      fontWeight: fontWeight,
      fontStyle: fontStyle,
      fileName: fileName,
      icon: icon,
    };
    var focusStyle = {
      backgroundColor: focusBackgroundColor,
      color: focusColor,
      fontWeight: focusFontWeight,
      fontStyle: focusFontStyle,
      fileName: focusfileName,
      icon: focusIcon,
    };

    this.eViewerObj.documentService
      .setDocumentTabStyle(this.state.docId, style, focusStyle)
      .then((response) => {
        console.log(response);
      });

    this.disableAllDiv();
  };

