  submitExportDetail = () => {
    if (this.eViewerObj === null) {
      this.eViewerObj = new eViewerApp(this.props.userName);
    }

    this.eViewerObj.setUserName(this.props.userName);

    if (
      this.state.startPage === undefined &&
      this.state.endPage === undefined
    ) {
      this.state.startPage = "";
      this.state.endPage = "";
    }

    let withComments = true;
    if (this.state.includeComments === "false") {
      withComments = false;
    }

    let exportData = {
      exportType: "EXPORT",
      pageFilters: this.state.pageOption,
      exportAs: this.state.doctype,
      includeAnnotations: true,
      includeWatermark: true,
      includeComments: withComments,
    };
    let fileType = this.state.doctype;
    this.eViewerObj.editingService
      .docExportWithOptions(exportData)
      .then((fileData) => {
        // fileData is an typed array (UINT8Array) holding the PDFStream

        // sample API to save the fileData
        this.exportFileData(fileType, fileData, this.state.DocName);
      });
    this.disableAllDiv();
  };

  exportFileData = (mimeType, view, docName) => {
    let file = null;
    if (navigator.msSaveBlob) {
      const downloadType =
        mimeType === "image/tiff" || mimeType === "tiff" ? "tiff" : "pdf";
      file = new Blob([view], { type: downloadType });
      navigator.msSaveOrOpenBlob(file, docName + "." + downloadType);
    } else {
      const downloadType =
        mimeType === "image/tiff" || mimeType === "tiff"
          ? "image/tiff"
          : "application/pdf";
      file = new Blob([view], { type: downloadType });

      const data = window.URL.createObjectURL(file);
      const link = document.createElement("a");
      document.body.appendChild(link);
      link.href = data;
      link.download = docName;
      link.click();

      setTimeout(() => {
        document.body.removeChild(link);
        window.URL.revokeObjectURL(data);
      }, 200);
    }
  };

