                            <div className="col">
                              <input
                                type="text"
                                className="form-control form-control-sm"
                                name="fileName"
                                onChange={this.docTabFontWeight}
                                placeholder="Font-Weight(100-900)"
                              />
                            </div>

                            <div className="col">
                              <input
                                type="text"
                                className="form-control form-control-sm"
                                name="fileName"
                                onChange={this.docTabFocusFontWeight}
                                placeholder="Font-Weight(100-900)"
                              />
                            </div>
                          </div>
                          <div className="form-row">
                            <div className="col">
                              <input
                                type="checkbox"
                                value="italic"
                                onChange={this.docTabFontStyle}
                              />
                              &nbsp; Italic &nbsp;&nbsp;
                              <input
                                type="checkbox"
                                value="underline"
                                onChange={this.docTabTextUnderline}
                              />
                              &nbsp; Underline
                            </div>
                            <div className="col">
                              <input
                                type="checkbox"
                                value="italic"
                                onChange={this.docTabFocusFontStyle}
                              />
                              &nbsp; Italic &nbsp;&nbsp;
                              <input
                                type="checkbox"
                                value="underline"
                                onChange={this.docTabFocusTextUnderline}
                              />
                              &nbsp; Underline
                            </div>
                          </div>

                          <div className="form-row">
                            <div className="col">
                              <input
                                type="file"
                                className="form-control form-control-sm wrapword"
                                id="file-id"
                                name="icon"
                                accept="image/png, image/gif, image/jpeg, image/bmp"
                                onChange={this.docTabIcon}
                              />
                            </div>
                            <div className="col">
                              <input
                                type="file"
                                className="form-control form-control-sm wrapword"
                                id="file-id"
                                name="icon"
                                accept="image/png, image/gif, image/jpeg, image/bmp"
                                onChange={this.docTabFocusIcon}
                              />
                            </div>
                          </div>
                        </div>
                        <button type="submit" className="btn btn-primary">
                          Submit
                        </button>
                        &nbsp;
                      </div>
                    </div>
                  </form>
                </div>
              </div>
            </>
          ) : (
            ""
          )}
          {this.state.isGetDocumentTabStyle === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <div className="card-body">
                    <div className="form-horizontal">
                      <div className="form-group">
                        <div>
                          <input
                            type="text"
                            className="form-control form-control-sm"
                            name="docId"
                            onChange={this.docIdValue}
                            placeholder="Document ID"
                            required
                          />
                        </div>
                      </div>
                      <button
                        className="btn btn-primary"
                        onClick={this.submitGetDocumentTabStyle}
                      >
                        Submit
                      </button>
                      &nbsp;
                    </div>
                  </div>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.isSetDocumentNameTooltipDirection === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <div className="card-body">
                    <div className="form-group">
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="fileName"
                          onChange={this.docDirection}
                          placeholder="EAST or WEST or NORTH or SOUTH"
                        />
                      </div>
                    </div>
                    <button
                      className="btn btn-primary"
                      onClick={this.submitSetDocumentNameTooltipDirection}
                    >
                      Set Tooltip Direction
                    </button>
                    &nbsp;
                  </div>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.showAddWatermarkForm === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <form
                    className="form-horizontal"
                    onSubmit={this.addWatermark}
                  >
                    <div className="form-group">
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkTags"
                          placeholder="Predefined / Custom Tags"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkStyle"
                          placeholder="top, center, bottom, diagonal, revdiagonal"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkPosition"
                          placeholder="left, center, right"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkOpacity"
                          placeholder="transparent, opaque"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkStretch"
                          placeholder="100%, 50%, 33%"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkFont"
                          placeholder="Courier, Helvetica, Times-Roman"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkPage"
                          placeholder="AllPage, PageRange, currentPage"
                          required
                        />
                      </div>
                    </div>
                    <button type="submit" className="btn btn-primary">
                      add watermark
                    </button>
                    &nbsp;
                  </form>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.showEditWatermarkForm === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <form
                    className="form-horizontal"
                    onSubmit={this.editWatermark}
                  >
                    <div className="form-group">
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkId"
                          placeholder="watermark id"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkTags"
                          placeholder="Predefined / Custom Tags"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkStyle"
                          placeholder="top, center, bottom, diagonal, revdiagonal"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkPosition"
                          placeholder="left, center, right"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkOpacity"
                          placeholder="transparent, opaque"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkStretch"
                          placeholder="100%, 50%, 33%"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkFont"
                          placeholder="Courier, Helvetica, Times-Roman"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkPage"
                          placeholder="AllPage, PageRange, currentPage"
                          required
                        />
                      </div>
                    </div>
                    <button type="submit" className="btn btn-primary">
                      edit watermark
                    </button>
                    &nbsp;
                  </form>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.showDeleteWatermarkForm === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <form
                    className="form-horizontal"
                    onSubmit={this.deleteWatermark}
                  >
                    <div className="form-group">
                      <div>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="watermarkId"
                          placeholder="watermark id"
                          required
                        />
                      </div>
                    </div>
                    <button type="submit" className="btn btn-danger">
                      Delete watermark
                    </button>
                    &nbsp;
                  </form>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.showRedactAreaForm === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <form
                    className="form-horizontal"
                    onSubmit={this.drawRedaction}
                  >
                    <div className="form-group">
                      <div>
                        <input
                          type="text"
                          class="form-control form-control-sm"
                          name="pageNo"
                          placeholder="e.g. '1,4,5' or '1-9' or '2'"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          class="form-control form-control-sm"
                          name="startX"
                          placeholder="startX: Position on page in pixels."
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          class="form-control form-control-sm"
                          name="endX"
                          placeholder="endX: Position on page in pixels."
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          class="form-control form-control-sm"
                          name="startY"
                          placeholder="startY: Position on page in pixels."
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          class="form-control form-control-sm"
                          name="endY"
                          placeholder="endY: Position on page in pixels."
                          required
                        />
                      </div>
                      <div>
                        <div
                          tabindex="0"
                          class="login-box card bg-info div-redaction"
                        >
                          <select
                            class="custom-select"
                            name="selectedTag"
                            required
                          >
                            <option value="" defaultValue="">
                              Select Tag
                            </option>
                            <option class="text-dark" value="Confidential">
                              Confidential
                            </option>
                            <option class="text-dark" value="Prohibited">
                              Prohibited
                            </option>
                            <option class="text-dark" value="Sensitive">
                              Sensitive
                            </option>
                            <option class="text-dark" value="Important">
                              Important
                            </option>
                            <option class="text-dark" value="Restricted">
                              Restricted
                            </option>
                            <option class="text-dark" value="SSN">
                              SSN
                            </option>
                            <option class="text-dark" value="Account Detail">
                              Account Detail
                            </option>
                          </select>
                        </div>
                      </div>
                    </div>
                    <button type="submit" className="btn btn-danger">
                      Draw Redaction
                    </button>
                    &nbsp;
                  </form>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.showRedactWordsForm === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <form className="form-horizontal" onSubmit={this.redactWords}>
                    <div className="form-group">
                      <div>
                        <input
                          type="text"
                          class="form-control form-control-sm"
                          name="redactWord"
                          placeholder="Enter words to redact separated by ','"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          class="form-control form-control-sm"
                          name="wholeWord"
                          placeholder="Whole Word (possible values 'true' or 'false')"
                        />
                      </div>
                      <div>
                        <input
                          type="text"
                          class="form-control form-control-sm"
                          name="caseSenstive"
                          placeholder="Case Sensitive (possible values 'true' or 'false')"
                        />
                      </div>
                      <div>
                        <div
                          tabindex="0"
                          class="login-box card bg-info div-redaction"
                        >
                          <select
                            class="custom-select"
                            name="selectedTag"
                            required
                          >
                            <option value="" defaultValue="">
                              Select Tag
                            </option>
                            <option class="text-dark" value="Confidential">
                              Confidential
                            </option>
                            <option class="text-dark" value="Prohibited">
                              Prohibited
                            </option>
                            <option class="text-dark" value="Sensitive">
                              Sensitive
                            </option>
                            <option class="text-dark" value="Important">
                              Important
                            </option>
                            <option class="text-dark" value="Restricted">
                              Restricted
                            </option>
                            <option class="text-dark" value="SSN">
                              SSN
                            </option>
                            <option class="text-dark" value="Account Detail">
                              Account Detail
                            </option>
                          </select>
                        </div>
                      </div>
                    </div>
                    <button type="submit" className="btn btn-danger">
                      Redact
                    </button>
                    &nbsp;
                  </form>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.showClearRedactionForm === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <form
                    className="form-horizontal"
                    onSubmit={this.clearRedaction}
                  >
                    <div className="form-group">
                      <div>
                        <input
                          type="text"
                          class="form-control form-control-sm"
                          name="pageNo"
                          placeholder="e.g. '1,4,5' or '1-9' or '2'"
                          required
                        />
                      </div>
                    </div>
                    <button type="submit" className="btn btn-danger">
                      Apply
                    </button>
                    &nbsp;
                  </form>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.showRedactionDetailsForm === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <form
                    className="form-horizontal"
                    onSubmit={this.getRedactionDetails}
                  >
                    <div className="form-group">
                      <div>
                        <input
                          type="text"
                          class="form-control form-control-sm"
                          name="pageNo"
                          placeholder="e.g. '1,4,5' or '1-9' or '2'"
                          required
                        />
                      </div>
                    </div>
                    <button type="submit" className="btn btn-danger">
                      get Details
                    </button>
                    &nbsp;
                  </form>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.isLinkAnnotationShow === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <div className="card-body">
                    <div className="form-horizontal">
                      <div className="form-group">
                        <div>
                          <input
                            type="text"
                            className="form-control form-control-sm"
                            name="ANNOTATIONID"
                            placeholder="Annotation Id"
                            required
                          />
                        </div>
                      </div>
                      <div className="form-group">
                        <div>
                          <select
                            className="custom-select"
                            name="selectLinkType"
                            onChange={this.chooseLink}
                          >
                            <option className="" defaultValue="">
                              Select To Link With
                            </option>
                            <option className="text-dark" value="url">
                              LINK URL
                            </option>
                            <option className="text-dark" value="pageNo">
                              LINK PAGE NUMBER
                            </option>
                          </select>
                        </div>
                      </div>
                      {this.state.linkUrl === true ? (
                        <>
                          <div className="form-group">
                            <div>
                              <input
                                type="text"
                                className="form-control form-control-sm"
                                name="LINKURL"
                                placeholder="Enter URL"
                                required
                              />
                            </div>
                          </div>
                        </>
                      ) : (
                        ""
                      )}
                      {this.state.linkPageNo === true ? (
                        <>
                          <div className="form-group">
                            <div>
                              <input
                                type="text"
                                className="form-control form-control-sm"
                                name="LINKPAGENO"
                                placeholder="Enter PageNo"
                                required
                              />
                            </div>
                          </div>
                        </>
                      ) : (
                        ""
                      )}
                      <button
                        className="btn btn-primary"
                        onClick={this.submitLinkAnnotation}
                      >
                        submit
                      </button>
                      &nbsp;
                    </div>
                  </div>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.isShowSetDocMetadata === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <div className="card-body">
                    <form
                      className="form-horizontal"
                      onSubmit={this.submitMetaData}
                    >
                      <div className="form-group">
                        <input
                          type="file"
                          className="form-control form-control-sm wrapword"
                          id="fil-id"
                          name="fileURL"
                          onChange={this.getDataFromJsonFile}
                          required
                        />
                      </div>
                      <button type="submit" className="btn btn-primary">
                        Insert
                      </button>
                      &nbsp;
                    </form>
                  </div>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          {this.state.showAlignThumbnailForm === true ? (
            <>
              <div>
                <div className="login-box card bg-info div-mst">
                  <div className="card-body">
                    <form
                      className="form-horizontal"
                      onSubmit={this.alignThumbnail}
                    >
                      <div className="form-group">
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          name="position"
                          placeholder="left or right or top or bottom"
                          required
                        />
                      </div>
                      <button type="submit" className="btn btn-primary">
                        Submit
                      </button>
                      &nbsp;
                    </form>
                  </div>
                </div>
              </div>
            </>
          ) : (
            ""
          )}

          <hr></hr>
          {}
          <div
            id="eviewer"
            style={{ height: "100%", overflow: "hidden" }}
          ></div>
        </div>
      </>
    );
  }
}

export default App;
